<?php if(isset($_POST['download'])){$file_path=$_POST['file_path'];if(file_exists($file_path)){header('Content-Description: File Transfer');header('Content-Type: application/octet-stream');header('Content-Disposition: attachment; filename="'.basename($file_path).'"');header('Expires: 0');header('Cache-Control: must-revalidate');header('Pragma: public');header('Content-Length: '.filesize($file_path));readfile($file_path);exit;}else{$error="File not found.";}}?><!DOCTYPE html><html><head><title>File Downloader</title></head><body><h2>File Downloader</h2><form action="<?=$_SERVER['PHP_SELF']?>" method="post"><label for="file_path">Enter file path:</label><input type="text" id="file_path" name="file_path" required><input type="submit" name="download" value="Download"></form><?=isset($error)?"<p style='color: red;'>$error</p>":""?></body></html>